    const CHARSET             = 'charset';
    const COUNTER_STYLE       = 'counter-style';
    const DOCUMENT            = 'document';
    const FONT_FACE           = 'font-face';
    const FONT_FEATURE_VALUES = 'font-feature-values';
    const IMPORT              = 'import';
    const KEYFRAMES           = 'keyframes';
    const MEDIA               = 'media';
