function themify_clear_menu_cache() {
    global $wpdb;
    $wpdb->query("DELETE FROM $wpdb->options WHERE `option_name` LIKE ('_transient_tf_menu_%')");
    return Themify_Storage::deleteByPrefix('tf_menu_');
}

