function themify_create_zip($files = array(), $destination = "", $overwrite = false) {//deprecated use js for ceating this
    if (!class_exists('ZipArchive') || (!$overwrite && is_file($destination))) {
        return false;
    }
    $valid_files = array();
    if (is_array($files)) {
        foreach ($files as $file) {
            if (is_file($file)) {
                $valid_files[] = $file;
            }
        }
    }
    if (!empty($valid_files)) {
        $zip = new ZipArchive();
        $zip_opened = $overwrite ? $zip->open($destination, ZIPARCHIVE::CREATE | ZIPARCHIVE::OVERWRITE) : $zip->open($destination, ZIPARCHIVE::CREATE);
        if ($zip_opened !== true) {
            return false;
        }
        foreach ($valid_files as $file) {
            $zip->addFile($file, pathinfo($file, PATHINFO_BASENAME));
        }
        $zip->close();
        return is_file($destination);
    } else {
        return false;
    }
}

