/**
 * Get breakpoints settings
 * if it's framework return customizer breakpoints,else if it's builder plugin builder breakpoints
 * @since 3.0.0
 * @return mixed array/int
 */
function themify_get_breakpoints($select = 'all', $max_min = false) {
    static $res = array();
    if (($select === 'all' && empty($res)) || (empty($res[$select]))) {
        $breakpoints = array(
            'tablet_landscape' => array(769, 1280),
            'tablet' => array(681, 768),
            'mobile' => 680
        );
        if ($max_min === true) {
            return $breakpoints;
        }
        foreach ($breakpoints as $bp => $value) {
            $v = themify_builder_get('setting-customizer_responsive_design_' . $bp, 'builder_responsive_design_' . $bp);
            if ('' != $v) {
                if (is_array($value)) {
                    $res[$bp] = array();
                    $res[$bp][0] = $value[0];
                    $res[$bp][1] = (int) $v;
                } else {
                    $res[$bp] = (int) $v;
                }
            } else {
                $res[$bp] = $value;
            }
        }
        $res['tablet'][0] = $res['mobile'] + 1;
        $res['tablet_landscape'][0] = $res['tablet'][1] + 1;
    }
    return 'all' === $select ? $res : $res[$select];
}

/**
 * Unserialize data if it is serialized
 *
 * If PHP supports it disables unserializing PHP objects in order to prevent object injection.
 *
 * @param string $original Maybe unserialized original, if is needed.
 * @return mixed Unserialized data can be any type.
 */
function themify_maybe_unserialize($original) {
    if (is_serialized($original)) { // don't attempt to unserialize data that wasn't serialized going in
        return version_compare(PHP_VERSION, '7.0.0') >= 0?@unserialize($original, array('allowed_classes' => false)):@unserialize($original);
    }

    return $original;
}

/**
 * Display the post video
 * Must be used inside the loop
 *
 * @since 2.7.3
 */
function themify_post_video($url = false, $echo = true) {
    $video_file = $url ? $url : themify_get('video_url');
    $output = '';
    if (!empty($video_file)) {
        if ('mp4' !== pathinfo($video_file, PATHINFO_EXTENSION)) {
            global $wp_embed;
            $output = $wp_embed->run_shortcode('[embed]' . $video_file . '[/embed]');
        } else {
            $output = '<div class="post-video">';
            $output .= do_shortcode('[video src="' . $video_file . '"]');
            $output .= '</div>';
        }
    }
    if ($echo === true) {
        echo $output;
    } else {
        return $output;
    }
}


function themify_get_embed($url,array $args){
    $reg='#(vimeo\.com|youtu(be\.com|\.be|be))\/(video\/|embed\/|watch\?v=|v\/)?([A-Za-z0-9._%-]*)(\&\S+)?\/?([A-Za-z0-9._%-]*)(\&\S+)?#i';
    preg_match($reg,$url,$m);
    if(!empty($m) && isset($m[1],$m[4])){
	$type=($m[1]==='youtube.com' || strpos($m[1],'youtu')!==false)?'youtube':($m[1]==='vimeo.com' || strpos($m[1],'vimeo')?'vimeo':false);
	if($type!==false){
	    $id=$m[4];
	    $query_args=array();
	    if($type!=='youtube' && isset($m[6])){//h query argument should be first in vimeo
		$query_args['h'] = $m[6];
	    }
	    $query_args+=array(
		'pip'=>1,
		'playsinline'=>1
	    );
	    $params=parse_url($url);
	    if(!empty($params['query'])){
		parse_str($params['query'], $output);
		unset($output['v']);
		$query_args+=$output;
		unset($output);
	    }
	    if($type==='youtube'){
		$allow='accelerometer;encrypted-media;gyroscope;picture-in-picture';
		$src='https://www.youtube';
		if(!empty($args['privacy'])){
		    $src.='-nocookie';
		}
		$query_args['autohide']=1;
		$query_args['border']=0;
		$query_args['wmode']='opaque';
		if(!empty($args['start'])){
		    $query_args['start']=(float)$args['start'];
		}
		if(!empty($args['end'])){
		    $query_args['end']=(float)$args['end'];
		}
		if(!empty($args['branding'])){
		    $query_args['modestbranding']=1;
		}
		$src.='.com/embed/'.$id;
	    }
	    else{
		
		$allow='fullscreen';
		$src='https://player.vimeo.com/video/'.$id;
		if(!empty($args['start'])){
		    $src.='#t='+$args['start'];
		}
		elseif(!empty($params['fragment'])){
		    $src.='#'+$params['fragment'];
		}
		if(!empty($args['privacy'])){
		    $query_args['dnt']=1;
		}
		$query_args['badge']=$query_args['title']=$query_args['portrait']=0;

	    }
	    unset(
		$params,
		$query_args['modestbranding'],
		$query_args['controls'],
		$query_args['loop'],
		$query_args['modestbranding'],
		$query_args['auoplay'],
		$query_args['mute'],
		$query_args['muted']
	    );
	    if(!empty($args['hide_controls'])){
		$query_args['controls']=0;
	    }

	    if(!empty($args['loop'])){
		$query_args['loop']=1;
		if(!isset($query_args['playlist'])){
		    $query_args['playlist']=$id;
		}
	    }
	    if(!empty($args['autoplay'])){
		$allow.=';autoplay';
		$query_args['autoplay']=1;
	    }
	    if(!empty($args['muted'])){
		if($type==='youtube'){
		    $query_args['mute']=1;
		}
		else{
		    $query_args['muted']=1;
		}
	    }
	    $lazy=!empty($args['disable_lazy'])?' data-no-script':'';
	    $src.='?'.http_build_query($query_args);
	    return '<iframe'.$lazy.' src="'.$src.'" allow="'.$allow.'" class="tf_abs tf_w tf_h"></iframe>';
	}

    }
    return '';
}
function themify_is_minify_enabled() {
    static $is = null;
    if ($is === null) {
        $is = ( defined('SCRIPT_DEBUG') && SCRIPT_DEBUG ) || defined('THEMIFY_DISABLE_MIN') || themify_is_dev_mode();
        if ($is === false) {
            $is = function_exists('themify_builder_get') && themify_builder_get('setting-script_minification-min', 'performance-script_minification-min') === null;

            if ($is === false && function_exists('get_rocket_option')) {
                $is = get_rocket_option('minify_js') ? true : false;
            }
        } else {
            $is = false;
        }
    }
    return $is;
}

/**
 * Load/Check minified file is exist
 *
 * @return string/bool
 */
function themify_enque($url, $check = false, $cdn = true) {
    static $is = null;
    if ($is === null) {
        $is = themify_is_minify_enabled();
    }
    $return = 0;
    if ($is === true) {
        $f = pathinfo($url);
        if (isset($f['extension']) && strpos($f['basename'], '.min.', 2) === false) {
            $name = $f['filename'] . '.min.' . $f['extension'];
	    if (is_file(trailingslashit(WP_CONTENT_DIR) . trailingslashit(str_replace(WP_CONTENT_URL, '', $f['dirname'])) . $name)) {
            if ($check === true) {
		$return = 1;
	    } else {
		$url = trailingslashit($f['dirname']) . $name;
	    }
        }
    }
    }

    if ($check === true) {
        return $return;
    }

    if ($cdn === true && function_exists('get_rocket_cdn_url')) {
        /**
         * WP Rocket CDN
         * @link https://wp-rocket.me
         */
        $url = get_rocket_cdn_url($url);
    }

    return $url;
}

